# map.py
import pygame
import sys

# === Configurações ===
TILE = 32
FPS = 60

TERRA = None
GRAMA = None
CEU = (135, 206, 235)
TILE_COLORS = { 0: CEU, 1: TERRA, 2: GRAMA }

def load_tiles():
    global TERRA, GRAMA
    try:
        TERRA = pygame.image.load('terra1.png').convert_alpha()
        GRAMA = pygame.image.load('grama1.png').convert_alpha()
        if TERRA.get_size() != (TILE, TILE):
            TERRA = pygame.transform.smoothscale(TERRA, (TILE, TILE))
        if GRAMA.get_size() != (TILE, TILE):
            GRAMA = pygame.transform.smoothscale(GRAMA, (TILE, TILE))
    except Exception as e:
        surf_terra = pygame.Surface((TILE, TILE))
        surf_terra.fill((150, 100, 50))
        surf_grama = pygame.Surface((TILE, TILE))
        surf_grama.fill((30, 160, 30))
        TERRA = surf_terra
        GRAMA = surf_grama

