ROWS = len(LEVEL)
COLS = len(LEVEL[0])

def draw_level(surface, level, camera=(0,0)):
    cam_x, cam_y = camera
    for row_i, row in enumerate(level):
        for col_i, ch in enumerate(row):
            x = col_i * TILE - cam_x
            y = row_i * TILE - cam_y
            if ch == 1:
                surface.blit(TERRA, (x, y))
            elif ch == 2:
                surface.blit(GRAMA, (x, y))
            elif isinstance(ch, tuple):
                pygame.draw.rect(surface, ch, (x, y, TILE, TILE))
            else:
                pygame.draw.rect(surface, CEU, (x, y, TILE, TILE))

def run(screen):
    clock = pygame.time.Clock()
    sw, sh = screen.get_size()

    # câmera (pode mover com setas/WASD)
    cam_x, cam_y = 0, 0
    cam_speed = 600  # px/s

    running = True
    while running:
        dt = clock.tick(FPS) / 1000

        for e in pygame.event.get():
            if e.type == pygame.QUIT:
                pygame.quit()
                sys.exit()
            if e.type == pygame.KEYDOWN:
                if e.key == pygame.K_ESCAPE:
                    running = False

        # inputs de câmera
        keys = pygame.key.get_pressed()
        move_x = 0
        move_y = 0
        if keys[pygame.K_LEFT] or keys[pygame.K_a]:
            move_x = -1
        if keys[pygame.K_RIGHT] or keys[pygame.K_d]:
            move_x = 1
        if keys[pygame.K_UP] or keys[pygame.K_w]:
            move_y = -1
        if keys[pygame.K_DOWN] or keys[pygame.K_s]:
            move_y = 1

        cam_x += int(move_x * cam_speed * dt)
        cam_y += int(move_y * cam_speed * dt)

        # limitar câmera (para não 'ver' área negativa ou muito além do mapa)
        max_x = max(0, COLS * TILE - sw)
        max_y = max(0, ROWS * TILE - sh)
        cam_x = max(0, min(cam_x, max_x))
        cam_y = max(0, min(cam_y, max_y))

        # desenho
        screen.fill(CEU)
        draw_level(screen, LEVEL, (cam_x, cam_y))
        pygame.display.flip()

    return
        
def get_collision_rects(tile_size=TILE, collide_tiles=(1, 2)):
    rects = []
    for row_i, row in enumerate(LEVEL):
        for col_i, ch in enumerate(row):
            if isinstance(ch, int) and ch in collide_tiles:
                rects.append(pygame.Rect(col_i * tile_size, row_i * tile_size, tile_size, tile_size))
    return rects


def get_merged_collision_rects(tile_size=TILE, collide_tiles=(1, 2)):
    rects = []
    for row_i, row in enumerate(LEVEL):
        start_col = None
        for col_i, ch in enumerate(list(row) + [None]):
            is_collide = isinstance(ch, int) and ch in collide_tiles
            if is_collide:
                if start_col is None:
                    start_col = col_i
            elif start_col is not None:
                x = start_col * tile_size
                y = row_i * tile_size
                w = (col_i - start_col) * tile_size
                rects.append(pygame.Rect(x, y, w, tile_size))
                start_col = None
    return rects

if __name__ == "__main__":
    pygame.init()
    screen = pygame.display.set_mode((0, 0), pygame.FULLSCREEN)
    load_tiles()
    pygame.display.set_caption("Mapa de Tiles - Teste")
    run(screen)
    pygame.quit()
