ROWS = len(LEVEL)
COLS = len(LEVEL[0])

def draw_level(surface, level, camera=(0,0)):
    cam_x, cam_y = camera
    sw, sh = surface.get_size()

    if bg_level_img:
        try:
            iw, ih = bg_level_img.get_size()
            scale = max(sw / iw, sh / ih)
            new_w = max(1, int(iw * scale))
            new_h = max(1, int(ih * scale))
            scaled = pygame.transform.smoothscale(bg_level_img, (new_w, new_h)).convert()
            x = (new_w - sw) // 2
            y = (new_h - sh) // 2
            crop = pygame.Surface((sw, sh)).convert()
            crop.blit(scaled, (-x, -y))
            surface.blit(crop, (0, 0))
        except Exception:
            surface.fill(CEU)
    elif bg_level_tile:
        try:
            tile_w, tile_h = bg_level_tile.get_size()
            if tile_h == 0:
                surface.fill(CEU)
            else:
                scale = sh / tile_h
                new_w = max(1, int(tile_w * scale))
                tile_surf = pygame.transform.smoothscale(bg_level_tile, (new_w, sh)).convert()
                x = 0
                while x < sw:
                    surface.blit(tile_surf, (x, 0))
                    x += new_w
        except Exception:
            surface.fill(CEU)
    else:
        surface.fill(CEU)
    for row_i, row in enumerate(level):
        for col_i, ch in enumerate(row):
            x = col_i * TILE - cam_x
            y = row_i * TILE - cam_y
            if ch == 1:
                surface.blit(TERRA, (x, y))
    for row_i, row in enumerate(level):
        for col_i, ch in enumerate(row):
            x = col_i * TILE - cam_x
            y = row_i * TILE - cam_y
            if ch == 2:  # grama
                surface.blit(GRAMA, (x, y))


def run(screen):
    clock = pygame.time.Clock()
    sw, sh = screen.get_size()

    cam_x, cam_y = 0, 0
    cam_speed = 600

    zoom = 1.8 

    running = True
    while running:
        dt = clock.tick(FPS) / 1000.0

        for e in pygame.event.get():
            if e.type == pygame.QUIT:
                pygame.quit()
                sys.exit()
            if e.type == pygame.KEYDOWN:
                if e.key == pygame.K_ESCAPE:
                    running = False

        keys = pygame.key.get_pressed()
        move_x = 0
        move_y = 0
        if keys[pygame.K_LEFT] or keys[pygame.K_a]:
            move_x = -1
        if keys[pygame.K_RIGHT] or keys[pygame.K_d]:
            move_x = 1
        if keys[pygame.K_UP] or keys[pygame.K_w]:
            move_y = -1
        if keys[pygame.K_DOWN] or keys[pygame.K_s]:
            move_y = 1

        cam_x += int(move_x * cam_speed * dt)
        cam_y += int(move_y * cam_speed * dt)
        if zoom <= 0:
            zoom = 1.0
        render_w = max(1, int(sw / zoom))
        render_h = max(1, int(sh / zoom))
        max_x = max(0, COLS * TILE - render_w)
        max_y = max(0, ROWS * TILE - render_h)
        cam_x = max(0, min(cam_x, max_x))
        cam_y = max(0, min(cam_y, max_y))
        render_surf = pygame.Surface((render_w, render_h)).convert()
        draw_level(render_surf, LEVEL, (cam_x, cam_y))
        zoomed = pygame.transform.smoothscale(render_surf, (sw, sh))
        screen.fill((0, 0, 0))
        screen.blit(zoomed, (0, 0))
        pygame.display.flip()

    return


        
def get_collision_rects(tile_size=TILE, collide_tiles=(1, 2)):
    rects = []
    for row_i, row in enumerate(LEVEL):
        for col_i, ch in enumerate(row):
            if isinstance(ch, int) and ch in collide_tiles:
                rects.append(pygame.Rect(col_i * tile_size, row_i * tile_size, tile_size, tile_size))
    return rects


def get_merged_collision_rects(tile_size=TILE, collide_tiles=(1, 2)):
    rects = []
    for row_i, row in enumerate(LEVEL):
        start_col = None
        for col_i, ch in enumerate(list(row) + [None]):
            is_collide = isinstance(ch, int) and ch in collide_tiles
            if is_collide:
                if start_col is None:
                    start_col = col_i
            elif start_col is not None:
                x = start_col * tile_size
                y = row_i * tile_size
                w = (col_i - start_col) * tile_size
                rects.append(pygame.Rect(x, y, w, tile_size))
                start_col = None
    return rects

if __name__ == "__main__":
    pygame.init()
    screen = pygame.display.set_mode((0, 0), pygame.FULLSCREEN)
    load_tiles()
    pygame.display.set_caption("Mapa de Tiles - Teste")
    run(screen)
    pygame.quit()
